const fs = require('fs');
exports.contentTypeis = function (ty,fn) {
    fs.readFile('./node_modules/content-type/mime.json', (err, data) => {
        if(err){
            console.log(err);
        }else{
            let allTY = JSON.parse(data.toString());
            fn(allTY[ty]);
            return allTY[ty];
        }
    })
};
